function.arg:
var s=ABS_SQLVALUE("SELECT COUNT(1) FROM JGCRM.OS_CURRENTSTEP T,JGCRM.LBWFCURRENTOWNER A WHERE T.ID = A.STEPID AND T.ENTRY_ID = ? AND T.STEP_ID = 210 AND A.OWNER = ?",[O_WORKFLOW.InstID,$Login.User]);
if (s>0){
